/* Determine if comment strings will be displayed */

string jobshow = "";
string salesshow = "";
string boxshow = "";

/* Setting for Auto Generated Comment */
jobshow = PCLookUp.DataLookup("RAPAutoGenComment_Control", "generate_auto", "JOB");
salesshow = PCLookUp.DataLookup("RAPAutoGenComment_Control", "generate_auto", "SALES");
boxshow = PCLookUp.DataLookup("RAPAutoGenComment_Control", "generate_auto", "BOXLABEL");

if (Inputs.chk_overridecomments.Value == false)
{
     /* Generate Comment Strings */

     /* Blank out comments if dynamic BAQ is used */
     if ((Inputs.rad_supplytype.Value == "2") && (Inputs.chk_nocommentgen.Value == true))
     {
         Inputs.var_jobcomments.Value = "";
         Inputs.var_linecomment.Value = "";
         Inputs.var_labeldesccalc.Value = "";
     }

      /* Post is manufactured */
      if ((Inputs.rad_supplytype.Value == "2") && (Inputs.chk_nocommentgen.Value == false))
      {
          /* Post Type */
          string fgcomment = "";
          fgcomment = "";
          fgcomment = PCLookUp.DataLookup("RAPPostHeadType", "fg_comment", Inputs.cmb_postheadtype.Value);

          /* Post Finish */
          string postfinishcomment = "";
          string postfinishcommentreplace = "";
          postfinishcomment = "";
          postfinishcommentreplace = "";
          if (Inputs.cmb_postfinish.Value != "")
          {
              /*postfinishcommentreplace = REPLACE(Inputs.cmb_postfinish.Value, "(", "+");*/
              /*postfinishcomment = TRIM(postfinishcommentreplace.Entry(0, "+"));*/

							postfinishcommentreplace = Inputs.cmb_postfinish.Value.Replace("cut", "+");
							postfinishcomment = postfinishcommentreplace.Trim('+');
          }

          /* Base Type */
          string basecomment = "";
          basecomment = "";
          basecomment = PCLookUp.DataLookup("RAPBaseRange", "base_comment", Inputs.cmb_baseselection.Value);

          if (jobshow == "TRUE")
          {
              Inputs.var_jobcomments.Value = fgcomment + " in " + postfinishcomment.ToLower() + " with " + basecomment.ToLower() + " base ";
          }

          if (salesshow == "TRUE")
          {
              Inputs.var_linecomment.Value = fgcomment + " in " + postfinishcomment.ToLower() + " with " + basecomment.ToLower() + " base ";
          }

          if (boxshow == "TRUE")
          {
              Inputs.var_labeldesccalc.Value = fgcomment + " in " + postfinishcomment.ToLower() + " with " + basecomment.ToLower() + " base ";
          }
      }

      /* Post is prebuilt *********************************************/
      if (Inputs.rad_supplytype.Value == "1")
      {
          string prebuiltpartfgdesc = "";
          prebuiltpartfgdesc = "";
          available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_sku.Value);
          if (available_part) {

						string Description = "";
						bool result = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_sku.Value, out Description);
						if(result)		
								prebuiltpartfgdesc = Description;          
          }

          if (jobshow == "TRUE")
          {
              Inputs.var_jobcomments.Value = prebuiltpartfgdesc;
          }

          if (salesshow == "TRUE")
          {
              Inputs.var_linecomment.Value = prebuiltpartfgdesc;
          }

          if (boxshow == "TRUE")
          {
              Inputs.var_labeldesccalc.Value = "";
          }
      }
}

