	 Inputs.var_custid.Value = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("ConfigTestModeCust", "testmode_cust", Context.CompanyID));

		/* Retrieves customer name using customer ID */
		bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, (int)Inputs.var_custid.Value, out CustName, out CustCountry, out currencyCode);
		if (available_Customer) {
				testmodecustname = CustName;
