  PageLeaveFunctions.SetNextInputPage(3);
}







// if Inputs.rad_supplytype.Value = "2" then do:
// (SetPrevInputPage(3)).
// End.
// 
// if Inputs.rad_supplytype.Value = "1" then do:
// (SetPrevInputPage(3)).
// End.
//

        /* (SetPrevInputPage(3)). */

        /* Retrieve the base currency set at Company level */
				string CurrencyCode = "";
        bool available_Currency = UDMethods.GetCompanyBaseCurrency(Context.CompanyID, out CurrencyCode);

        if (available_Currency) {
            Inputs.var_basecurrency.Value = CurrencyCode;
        }

        /* Retrieves customer details using customer ID */
				string Name = "";
				string Country = "";
				string currencycode = "";

        bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, (int)Inputs.var_custid.Value, out Name, out Country, out currencycode);

        if (available_Customer) {
            Inputs.var_customername.Value = Name;
        }

        /* Retrieve based on the configurator mode context */
        string currencymode = "";
        currencymode = Context.Entity;

        if (currencymode == "JOB")
        {
            if (available_Customer) {
							Inputs.var_custcurrency.Value = currencycode;
							Inputs.var_custcurrency1.Value = currencycode;
            }
        }

        /* ********************************************************** */

        /* Sets flag for prompt when condition to indicate if segment is a pricing factor */

        string seg2lookup = "";
        string seg3lookup = "";
        string seg4lookup = "";
        string seg5lookup = "";
        string seg6lookup = "";
        string seg7lookup = "";
        string seg8lookup = "";
        string seg9lookup = "";
        string seg10lookup = "";
        string seg11lookup = "";
        string seg12lookup = "";

        string seg2csvref = "";
        string seg3csvref = "";
        string seg4csvref = "";
        string seg5csvref = "";
        string seg6csvref = "";
        string seg7csvref = "";
        string seg8csvref = "";
        string seg9csvref = "";
        string seg10csvref = "";
        string seg11csvref = "";
        string seg12csvref = "";

				bool available_part = false;

        /* ************************************************************************************ */

        /* Segment 1 - Base Price for Product Code */
        if (Inputs.rad_supplytype.Value == "2")
        {
            Inputs.var_seg1code.Value = Inputs.var_smartstring.Value.Entry(0, "|");
            Inputs.var_seg1upcharge.Value = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("RAPListPriceSegmentControl", "base_listprice", Inputs.cmb_postheadtype.Value));
        }

        if (Inputs.rad_supplytype.Value == "1")
        {
						string PricePerCode = "";
						decimal UnitPrice = 0m;
            Inputs.var_seg1code.Value = Inputs.var_sku.Value;
            available_part = UDMethods.GetPartPrice(Context.CompanyID, Inputs.var_seg1code.Value, out UnitPrice, out PricePerCode);

            if (available_part) {
                Inputs.var_seg1upcharge.Value = UnitPrice;
            }
        }

        /* ************************************************************************************ */

        /* Seg2 - Base Style */

        seg2lookup = PCLookUp.DataLookup("RAPListPriceSegmentControl", "seg2", Inputs.var_seg1code.Value);
        if ((seg2lookup != "False") || (seg2lookup != ""))
        {
            Inputs.var_seg2active.Value = true;
            seg2csvref = PCLookUp.DataLookup("RAPListPriceSegmentControl", "seg2", "csv_columnref");
            //FieldToolTip('Inputs.var_seg2active.Value', seg2lookup + seg2csvref);
					  Inputs.var_seg2active.ToolTip = seg2lookup + seg2csvref;
        }
        if ((seg2lookup == "False") || (seg2lookup == ""))
        {
            Inputs.var_seg2active.Value = false;
            //FieldToolTip('Inputs.var_seg2active.Value', "");
						Inputs.var_seg2active.ToolTip = "";
        }

        if (Inputs.var_seg2active.Value == true)
        {
            /* Action Here */
            if (Inputs.rad_supplytype.Value == "2")
            {
								Inputs.var_seg2code.Value = Inputs.cmb_baseselection.Value;
								Inputs.var_seg2upcharge.Value = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("RAPBaseRange", seg2lookup + seg2csvref, Inputs.cmb_baseselection.Value));
            }
        }

        if (Inputs.var_seg2active.Value == false)
        {
            Inputs.var_seg2code.Value = "";
            Inputs.var_seg2upcharge.Value = 0;
        }

        /* ************************************************************************************ */

        /* Seg3 - Base Cover Finish */

