        }

        if ((Inputs.var_fgstockpart.Value != "") && (Inputs.var_fgmodecalc.Value == "JOB"))
        {
            Inputs.var_optouse.Value = PCLookUp.DataLookup("TBSPostSelection_mfg", "fromstock_oper_code", "");
            Inputs.var_op10time.Value = 0;
