  available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_fgnum.Value, out desc);
  if (available_part) {

		Inputs.var_fgdesc.Value = desc;

		string warrantyCode = "";
		bool available_part_warranty = UDMethods.GetPartWarrantyCode(Context.CompanyID, Inputs.var_fgnum.Value, out warrantyCode);
		if (available_part_warranty)
			Inputs.chr_warranty.Value = warrantyCode;

	  /*Inputs.var_netweight.Value = part.netweight;  */
		decimal weight = 0m;
		UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_fgnum.Value, out weight);
	  Inputs.var_netweight.Value = weight;

		Inputs.var_smartstring.Value = "Not Applicable as Parts Master Record";

	}

}	/* END IF NO JOB REQUIRED */

/* Calculate Commodity Code ***********************************************************************************/

string commoditycheck = "";

/* First Option */
/* Commodity code based on assignment in part record */
if (Inputs.var_commodity.Value=="") {
  string commodityCode = "";
  available_part = UDMethods.GetPartCommodityCode(Context.CompanyID, Inputs.var_sku.Value, out commodityCode);

  if (available_part) {
	  Inputs.var_commodity.Value = commodityCode;
  }
}

/* Second Option */
if (Inputs.var_commodity.Value=="") {
  string partclass = "";
  available_part = UDMethods.GetPartClass(Context.CompanyID, Inputs.var_sku.Value, out partclass);

  string CommodityCode = "";
  /* As Part Class is used to assign Commodity Code.*/
  if ((Inputs.var_commodity.Value=="") && (partclass!="")) {
    bool available_partclass = UDMethods.GetPartCommodityCode(Context.CompanyID, partclass, out CommodityCode);
    if (available_part) {
			Inputs.var_commodity.Value = CommodityCode;
    }
  }

  /* Last Option */
  /* Returns Commodity Code from CSV */
  if (Inputs.var_commodity.Value=="") {
    Inputs.var_commodity.Value = PCLookUp.DataLookup("TENUS_FG_MasterTable", "commodity_code", Inputs.var_fgprefix.Value);

    /* Verifies Commodity code exists in IComCode Table within Epicor */

    if (Inputs.var_commodity.Value!="") {
			bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID, Inputs.var_commodity.Value, out commoditycheck);
    }

    if ((Inputs.var_commodity.Value!=commoditycheck) && (Inputs.chk_erroron.Value==true)) {
      /* English */

			MessageBox.Show("The Configurator has assigned a Commodity code that does not have a corresponding Epicor record. Please contact your Epicor Administrator to resolve this issue.", "Error", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);
			PageLeaveFunctions.SetNextInputPage(1);
			Args.Cancel = true;

    }
  }
}

if ((Inputs.var_commodity.Value=="") && (Inputs.chk_erroron.Value==true)) {

	MessageBox.Show("The Configurator has been unable to determine the Commodity Code for the chosen configuration. Please contact your Epicor Administrator to resolve this issue.", "Error", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);
	PageLeaveFunctions.SetNextInputPage(1);
	Args.Cancel = true;

}
else if ((Inputs.var_commodity.Value!="") || (Inputs.var_commodity.Value==commoditycheck )) {
  PageLeaveFunctions.SetNextInputPage(5);
}

/* Validity Routines **********************************************************************************************************************************************************/

/* Check that a FG Num has been passed */
if ((Inputs.var_fgnum.Value == "") && (Inputs.chk_erroron.Value == true)) {

	MessageBox.Show("No Finished Goods number has been calculated for the chosen configuration. Please contact your Epicor Administrator to resolve this issue.", "Error", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);
	PageLeaveFunctions.SetNextInputPage(1);
	Args.Cancel = true;
}

/* Checks if FG Number passed to sales line is less than 50 Characters */
int smartcount = Inputs.var_sku.Value.Length;

if (smartcount > 50) {
  Inputs.var_smart2.Value = Inputs.var_fgnum.Value + "+ XxChar_Count_ExceededxX";
  Inputs.chk_makedirect.Value = true;
}
if (smartcount <= 50) {
  Inputs.var_smart2.Value = Inputs.var_fgnum.Value;
}

