fgdesc1="";

string description = "";
if (fgdesccheck == "") {
  available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_sku.Value, out description);
	if (available_part) {
    fgdesc = description;
  }
  if (fgdesc!="") {
    Inputs.var_fgdesc.Value = fgdesc1;
    Inputs.var_fgvalidity.Value = "OK";
  }
  if ((fgdesc1 =="") && (Inputs.chk_erroron.Value==true)) {
    /* English */

		MessageBox.Show("No Description has been returned for the Finished Goods Number.", "Note", MessageBoxButtons.OK,  MessageBoxIcon.Information);
		PageLeaveFunctions.SetNextInputPage(1);
		Args.Cancel = true;

  }
