        string mode1 = "";
        mode1 = Context.Entity;

				bool available_part = false;
				decimal availqty = 0m;
				decimal availqty1 = 0m;
				string assystockmodelookup = "";



        /* mode1 = "Quote". */

        if ((mode1 != "Quote") && (Inputs.var_fgmodecalc.Value == "JOB"))
        {
            /* Determine the FG mode to operate in */

            assystockmodelookup = PCLookUp.DataLookup("TBSAssembly_Stocking_Mode", "mode", "fgassy");

            /* Mode has been declared as Make Direct */
            if (Inputs.chk_makedirect.Value == true)
            {
                Inputs.chk_rework.Value = true;
                Inputs.var_configtype.Value = "MFG";
            }

            if (Inputs.chk_makedirect.Value == false)
            {
                Inputs.chk_rework.Value = false;

                /* Calculate the bool available_qty for the finished assembly */
                bool partrecordexists = false;
                string partrecordtype = "";

								string PartTypeCode = "";
								available_part = UDMethods.GetPartTypeCode(Context.CompanyID, Inputs.var_sku.Value, out PartTypeCode);
                if (available_part) {
                    partrecordexists = true;
                    partrecordtype = PartTypeCode;
                }

                if (partrecordexists == true)
                {
                    decimal qtyonhand = 0m;
                    decimal demandqty = 0m;
                    bool fromstock = false;

										Inputs.var_fgparttype.Value = partrecordtype;
										decimal OnHandQty;
										decimal DemandQty;

										/* Search for a FG Part record and calculate the bool available_qty's */
										bool available_PartQty = UDMethods.GetPartQty(Context.CompanyID, Inputs.var_sku.Value, out OnHandQty, out DemandQty);
										if (available_PartQty) {
												qtyonhand = OnHandQty;
												demandqty = DemandQty;
												availqty = qtyonhand - demandqty;
										}

                    /* Make Direct Mode Only */
                    if (assystockmodelookup == "NONE")
                    {
                        Inputs.chk_rework.Value = true;
                        Inputs.var_configtype.Value = "MFG";
                    }

                    /* only take fg assy from stock if the bool available_qty fulfills the full demand */
                    if (assystockmodelookup == "FULL")
                    {
                         if (availqty < Inputs.dec_soqty.Value) { fromstock = false; }
                        if (availqty == Inputs.dec_soqty.Value) { fromstock = true; }
                        if (availqty > Inputs.dec_soqty.Value) { fromstock = true; }

                        /* Manufactured Part Decision */
                        if (partrecordtype == "M")
                        {
                            if (fromstock == true) { Inputs.chk_rework.Value = false; }
                            if (fromstock == false) { Inputs.chk_rework.Value = true; }
                            /* Determine Config Type */
                            if (fromstock == true) { Inputs.var_configtype.Value = "M-STK"; }
                            if (fromstock == false) { Inputs.var_configtype.Value = "MFG"; }
                        }

                        if (partrecordtype == "P")
                        {
														/* Rqd due to a bug in CTP which ignores the sales release if a purchased part record exists */
                            if (fromstock == true) { Inputs.chk_rework.Value = false; }
                            if (fromstock == false) { Inputs.chk_rework.Value = false; }
                            /* Determine Config Type */
                            Inputs.var_configtype.Value = "PUR";
                        }

                        /* will take fg assy from stock even if the bool available_qty does not fulfill the full demand */
                        if (assystockmodelookup == "ANY")
                        {
                            if (availqty > 1) { fromstock = true; }
                            if (availqty == 0) { fromstock = false; }

                            /* Manufactured Part Decision */
                            if (partrecordtype == "M")
                            {
                                if (fromstock == true) { Inputs.chk_rework.Value = false; }
                                if (fromstock == false) { Inputs.chk_rework.Value = true; }
                                /* Determine Config Type */
                                if (fromstock == true) { Inputs.var_configtype.Value = "M-STK"; }
                                if (fromstock == false) { Inputs.var_configtype.Value = "MFG"; }
                            }

                            if (partrecordtype == "P")
                            {
																/* Rqd due to a bug in CTP which ignores the sales release if a purchased part record exists */;
                                if (fromstock == true) { Inputs.chk_rework.Value = false; }
                                if (fromstock == false) { Inputs.chk_rework.Value = false; }
                                /* Determine Config Type */
                                Inputs.var_configtype.Value = "PUR";
                            }
                        }
                    }
								}

                if (partrecordexists == false)
                {
                    Inputs.chk_rework.Value = true;
                    /* Determine Config Type */
                    Inputs.var_configtype.Value = "MFG";
                }

						}

				}

        /* **************************************************************************************** */

        /* Keyed in the quote environment and set as Make Direct from Components only */
        if ((mode1 == "Quote") && (Inputs.var_fgmodecalc.Value == "JOB"))
        {
            Inputs.chk_rework.Value = true;
            Inputs.var_configtype.Value = "MFG";
        }

        /* PreBuilt Supply */
        if ((mode1 == "Quote") && (Inputs.var_fgmodecalc.Value == "NOJOB"))
        {
            Inputs.chk_rework.Value = false;
            Inputs.var_configtype.Value = "PUR";
        }

        /* **************************************************************************************** */


        /* Prebuilt only */
				if ((mode1 != "Quote") && (Inputs.var_fgmodecalc.Value == "NOJOB"))
        {
            if (Inputs.chk_makedirect.Value == false)
            {
                Inputs.chk_rework.Value = false;
                /* Calculate the bool available_qty for the finished assembly */
                bool partrecordexists1 = false;
                string partrecordtype1 = "";

                string PartTypeCode = "";
                available_part = UDMethods.GetPartTypeCode(Context.CompanyID, Inputs.var_sku.Value, out PartTypeCode);
                if (available_part)
                {
                    partrecordexists1 = true;
                    partrecordtype1 = PartTypeCode;
                }

                if (partrecordexists1 == true)
                {
                    decimal qtyonhand1 = 0m;
                    decimal demandqty1 = 0m;
                    bool fromstock1 = false;

                    Inputs.var_fgparttype.Value = partrecordtype1;
                    decimal OnHandQty;
                    decimal DemandQty;

                    /* Search for a FG Part record and calculate the bool available_qty's */
                    bool available_PartQty = UDMethods.GetPartQty(Context.CompanyID, Inputs.var_sku.Value, out OnHandQty, out DemandQty);
                    if (available_PartQty)
                    {
                        qtyonhand1 = OnHandQty;
                        demandqty1 = DemandQty;
                        availqty1 = qtyonhand1 - demandqty1;
                    }

                    /* only take fg assy from stock if the bool available_qty fulfills the full demand */
                    if (assystockmodelookup == "FULL")
                    {
                        if (availqty1 < Inputs.dec_soqty.Value) { fromstock1 = false; }
                        if (availqty1 == Inputs.dec_soqty.Value) { fromstock1 = true; }
                        if (availqty1 > Inputs.dec_soqty.Value) { fromstock1 = true; }

                        /* Manufactured Part Decision */
                        if (partrecordtype1 == "M")
                        {
                            if (fromstock1 == true) { Inputs.chk_rework.Value = false; }
                            if (fromstock1 == false) { Inputs.chk_rework.Value = false; }
                            /* Determine Config Type */
                            if (fromstock1 == true) { Inputs.var_configtype.Value = "M-STK"; }
                            if (fromstock1 == false) { Inputs.var_configtype.Value = "MFG"; }
                        }

                        if (partrecordtype1 == "P")
                        {
                            /* Rqd due to a bug in CTP which ignores the sales release if a purchased part record exists */
                            if (fromstock1 == true) { Inputs.chk_rework.Value = false; }
                            if (fromstock1 == false) { Inputs.chk_rework.Value = false; }
                            /* Determine Config Type */
                            Inputs.var_configtype.Value = "PUR";
                        }

                        /* will take fg assy from stock even if the bool available_qty does not fulfill the full demand */
                        if (assystockmodelookup == "ANY")
                        {
                            if (availqty1 > 1) { fromstock1 = true; }
                            if (availqty1 == 0) { fromstock1 = false; }

                            /* Manufactured Part Decision */
                            if (partrecordtype1 == "M")
                            {
                                if (fromstock1 == true) { Inputs.chk_rework.Value = false; }
                                if (fromstock1 == false) { Inputs.chk_rework.Value = false; }
                                /* Determine Config Type */
                                if (fromstock1 == true) { Inputs.var_configtype.Value = "M-STK"; }
                                if (fromstock1 == false) { Inputs.var_configtype.Value = "MFG"; }
                            }

                            if (partrecordtype1 == "P")
                            {
                                if (fromstock1 == true) { Inputs.chk_rework.Value = false; }
                                if (fromstock1 == false) { Inputs.chk_rework.Value = false /* Rqd due to a bug in CTP which ignores the sales release if a purchased part record exists */; }
                                /* Determine Config Type */
                                Inputs.var_configtype.Value = "PUR";
                            }
                        }
                    }
                }
            }
        }

				/* String to indicate on Last Page that the fg assy will come from a stocked part */

        if (Inputs.chk_rework.Value == false)
        {
            string assystring = "";
            if (Inputs.var_fgmodecalc.Value == "JOB") { assystring = Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_sku.Value + " will be taken from bool available_stock of " + availqty.ToString() + "."; }
            if (Inputs.var_fgmodecalc.Value == "NOJOB") { assystring = Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_sku.Value + " will be taken from bool available_stock of " + availqty1.ToString()  + "."; }
            Inputs.var_fgvalidity.Value = assystring;

            /* Update BOM FG Part */
            Inputs.var_fgstockpart.Value = Inputs.var_sku.Value;

            /* Configure unwanted BOM parts */
            Inputs.var_part1.Value = "";
            Inputs.var_part2.Value = "";
            Inputs.var_part3.Value = "";
            Inputs.var_part4.Value = "";
            Inputs.var_part5.Value = "";
            Inputs.var_part6.Value = "";
            Inputs.var_part7.Value = "";
            Inputs.var_part8.Value = "";
            Inputs.var_part9.Value = "";
            Inputs.var_part10.Value = "";
            Inputs.var_part11.Value = "";
            Inputs.var_part12.Value = "";
            Inputs.var_part13.Value = "";
            Inputs.var_part14.Value = "";
            Inputs.var_part15.Value = "";
            Inputs.var_partqty1.Value = 0;
            Inputs.var_partqty2.Value = 0;
            Inputs.var_partqty3.Value = 0;
            Inputs.var_partqty4.Value = 0;
            Inputs.var_partqty5.Value = 0;
            Inputs.var_partqty6.Value = 0;
            Inputs.var_partqty7.Value = 0;
            Inputs.var_partqty8.Value = 0;
            Inputs.var_partqty9.Value = 0;
            Inputs.var_partqty10.Value = 0;
            Inputs.var_partqty11.Value = 0;
            Inputs.var_partqty12.Value = 0;
            Inputs.var_partqty13.Value = 0;
            Inputs.var_partqty14.Value = 0;
            Inputs.var_partqty15.Value = 0;

            Inputs.var_bomvalidity.Invisible= true;
            Inputs.var_bomstatus.Invisible= true;
            Inputs.var_weightlookup.Invisible= true;
            Inputs.var_part1.Invisible= true;
            Inputs.var_part2.Invisible= true;
            Inputs.var_part3.Invisible= true;
            Inputs.var_part4.Invisible= true;
            Inputs.var_part5.Invisible= true;
            Inputs.var_part6.Invisible= true;
            Inputs.var_part7.Invisible= true;
            Inputs.var_part8.Invisible= true;
            Inputs.var_part9.Invisible= true;
            Inputs.var_part10.Invisible= true;
            Inputs.var_part11.Invisible= true;
            Inputs.var_part12.Invisible= true;
            Inputs.var_part13.Invisible= true;
            Inputs.var_part14.Invisible= true;
            Inputs.var_part15.Invisible= true;

            Inputs.var_partqty1.Invisible= true;
            Inputs.var_partqty2.Invisible= true;
            Inputs.var_partqty3.Invisible= true;
            Inputs.var_partqty4.Invisible= true;
            Inputs.var_partqty5.Invisible= true;
            Inputs.var_partqty6.Invisible= true;
            Inputs.var_partqty7.Invisible= true;
            Inputs.var_partqty8.Invisible= true;
            Inputs.var_partqty9.Invisible= true;
            Inputs.var_partqty10.Invisible= true;
            Inputs.var_partqty11.Invisible= true;
            Inputs.var_partqty12.Invisible= true;
            Inputs.var_partqty13.Invisible= true;
            Inputs.var_partqty14.Invisible= true;
            Inputs.var_partqty15.Invisible= true;

				}

        if (Inputs.chk_rework.Value == true)
        {
            Inputs.var_fgvalidity.Value = "Make Direct";
            Inputs.var_bomstatus.Invisible = false;
            Inputs.var_weightlookup.Invisible = false;
            Inputs.var_bomvalidity.Invisible = false;
            Inputs.var_bomvalidity.Value = "Make Direct";

            /* Update BOM FG Part */
            Inputs.var_fgstockpart.Value = "";


            if ((Inputs.var_bomstatus.Value == "** ERROR ** A component in the BOM has no Epicor Part Master Record.") && (Inputs.chk_erroron.Value == true))
            {
								MessageBox.Show("A Component in the BOM does not exist in the Parts Master table, or the record has been made inactive. Please contact the Operations department to resolve this issue.", "Error", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);


            }
				}

        /* **************************************************************************************** */

        /* Calculates Net Weight */

        decimal CompWeight1 = 0m;
        decimal CompWeight2 = 0m;
        decimal CompWeight3 = 0m;
        decimal CompWeight4 = 0m;
        decimal CompWeight5 = 0m;
        decimal CompWeight6 = 0m;
        decimal CompWeight7 = 0m;
        decimal CompWeight8 = 0m;
        decimal CompWeight9 = 0m;
        decimal CompWeight10 = 0m;
        decimal CompWeight11 = 0m;
        decimal CompWeight12 = 0m;
        decimal CompWeight13 = 0m;
        decimal CompWeight14 = 0m;
        decimal CompWeight15 = 0m;

				decimal PartWeight = 0m;

        if (Inputs.var_part1.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part1.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight1 = PartWeight * Inputs.var_partqty1.Value;
        }

        if (Inputs.var_part2.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part2.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight2 = PartWeight * Inputs.var_partqty2.Value;
        }

        if (Inputs.var_part3.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part3.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight3 = PartWeight * Inputs.var_partqty3.Value;
        }

        if (Inputs.var_part4.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part4.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight4 = PartWeight * Inputs.var_partqty4.Value;
        }

        if (Inputs.var_part5.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part5.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight5 = PartWeight * Inputs.var_partqty5.Value;
        }

        if (Inputs.var_part6.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part6.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight6 = PartWeight * Inputs.var_partqty6.Value;
        }

        if (Inputs.var_part7.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part7.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight7 = PartWeight * Inputs.var_partqty7.Value;
        }

        if (Inputs.var_part8.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part8.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight8 = PartWeight * Inputs.var_partqty8.Value;
        }

        if (Inputs.var_part9.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part9.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight9 = PartWeight * Inputs.var_partqty9.Value;
        }

        if (Inputs.var_part10.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part10.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight10 = PartWeight * Inputs.var_partqty10.Value;
        }

        if (Inputs.var_part11.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part11.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight11 = PartWeight * Inputs.var_partqty11.Value;
        }

        if (Inputs.var_part12.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part12.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight12 = PartWeight * Inputs.var_partqty12.Value;
        }

        if (Inputs.var_part13.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part13.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight13 = PartWeight * Inputs.var_partqty13.Value;
        }

        if (Inputs.var_part14.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part14.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight14 = PartWeight * Inputs.var_partqty14.Value;
        }

        if (Inputs.var_part15.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part15.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight15 = PartWeight * Inputs.var_partqty15.Value;
        }

        /* Totals the individual component weights if make direct */
        if (Inputs.chk_rework.Value == true)
        {

            /* Totals the individual component weights */
            if (Inputs.var_fgmodecalc.Value == "JOB")
            {
                Inputs.var_netweight.Value = CompWeight1 + CompWeight2 + CompWeight3 + CompWeight4 + CompWeight5 + CompWeight6 + CompWeight7 + CompWeight8 + CompWeight9 + CompWeight10 + CompWeight11 + CompWeight12 + CompWeight13 + CompWeight14 + CompWeight15;

                /* Used to display Netweight list incase verification is required */
                Inputs.var_weightlookup.Value = "1(" + CompWeight1.ToString() + ")" + " - 2(" + CompWeight2.ToString() + ")" + " - 3(" + CompWeight3.ToString() + ")" + " - 4(" + CompWeight4.ToString() + ")" + " - 5(" + CompWeight5.ToString() + ")" + " - 6(" + CompWeight6.ToString() + ")" + " - 7(" + CompWeight7.ToString() + ")" + " - 8(" + CompWeight8.ToString() + ")" + " - 9(" + CompWeight9.ToString() + ")" + " - 10(" + CompWeight10.ToString() + ")" + " - 11(" + CompWeight11.ToString() + ")" + " - 12(" + CompWeight12.ToString() + ")" + " - 13(" + CompWeight13.ToString() + ")" + " - 14(" + CompWeight14.ToString() + ")" + " - 15(" + CompWeight15.ToString() + ")";
            }
        }


        if ((Inputs.chk_rework.Value == false) && (Inputs.var_fgmodecalc.Value == "JOB"))
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_sku.Value, out PartWeight);
            if (available_part) {
                Inputs.var_netweight.Value = PartWeight;
            }
        }

        /* Set the default Operation */
        if (Inputs.chk_rework.Value == false)
        {
            Inputs.var_optouse.Value = Inputs.var_op1.Value;
        }

        if ((Inputs.chk_rework.Value == true) && (Inputs.var_fgstockpart.Value == ""))
        {
            Inputs.var_optouse.Value = Inputs.var_op1.Value;
            /* Sets the Operation Std Time */
            Inputs.var_op10time.Value = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_op_time_min", Inputs.var_fgprefix.Value));
        }

        if ((Inputs.var_fgstockpart.Value != "") && (Inputs.var_fgmodecalc.Value == "JOB"))
        {
            Inputs.var_optouse.Value = PCLookUp.DataLookup("TBSPostSelection_mfg", "fromstock_oper_code", "");
            Inputs.var_op10time.Value = 0;
        }
